public void getUserDetails(String username) throws SQLException {
    Connection conn = DriverManager.getConnection("jdbc:example://localhost:3306/mydatabase", "user", "password");
    Statement stmt = conn.createStatement();

    // Vulnerable to SQL Injection
    String query = "SELECT * FROM users WHERE username = '" + username + "'";

    // Example of how the query can be abused if username is not sanitized
    ResultSet rs = stmt.executeQuery(query); 

    while (rs.next()) {
