import { useState, useEffect } from 'react';
import { AlertCircle, MapPin, Sun, Cloud, Wind, Droplets } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';

export default function WeatherChatbot() {
  const [messages, setMessages] = useState([
    { type: 'bot', content: 'Hello! I can help you check the weather. Could you please share your location? You can enter a city name, ZIP code, or allow location services.' }
  ]);
  const [userInput, setUserInput] = useState('');
  const [location, setLocation] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleUserInput = (e) => {
    e.preventDefault();
    if (!userInput.trim()) return;

    // Add user message to chat
    setMessages(prev => [...prev, { type: 'user', content: userInput }]);

    // Process location input
    if (!location) {
      handleLocationInput(userInput);
    } else {
      handleWeatherQuery(userInput);
    }

    setUserInput('');
  };

  const handleLocationInput = (input) => {
    setLoading(true);
    // Simulate location processing
    setTimeout(() => {
      setLocation(input);
      setMessages(prev => [...prev, {
        type: 'bot',
        content: `Thanks! I've got your location as ${input}. What would you like to know? You can ask about:
        • Current weather
        • Daily forecast
        • Weekly forecast
        • Specific conditions (temperature, humidity, wind)`
      }]);
      setLoading(false);
    }, 1000);
  };

  const handleWeatherQuery = (query) => {
    setLoading(true);
    // Simulate weather data fetching
    setTimeout(() => {
      const mockWeatherData = {
        temperature: '72°F',
        condition: 'Sunny',
        humidity: '45%',
        windSpeed: '8 mph'
      };
      setWeatherData(mockWeatherData);

      let response = '';
      if (query.toLowerCase().includes('current')) {
        response = `Current conditions in ${location}:
        Temperature: ${mockWeatherData.temperature}
        Conditions: ${mockWeatherData.condition}
        Humidity: ${mockWeatherData.humidity}
        Wind Speed: ${mockWeatherData.windSpeed}`;
      } else if (query.toLowerCase().includes('forecast')) {
        response = `Here's your forecast for ${location}. Would you like to see the daily or weekly view?`;
      }

      setMessages(prev => [...prev, { type: 'bot', content: response }]);
      setLoading(false);
    }, 1000);
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white rounded-lg shadow-lg p-6">
        {/* Privacy Notice */}
        <Alert className="mb-4">
          <AlertCircle className="h-4 w-4" />
          <AlertTitle>Privacy Notice</AlertTitle>
          <AlertDescription>
            Your location data is used only for weather forecasting and is never stored or shared.
          </AlertDescription>
        </Alert>

        {/* Chat Messages */}
        <div className="space-y-4 mb-4 h-96 overflow-y-auto">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`rounded-lg p-3 max-w-xs ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                {message.content}
              </div>
            </div>
          ))}
          {loading && (
            <div className="text-center">
              <div className="animate-pulse">Processing...</div>
            </div>
          )}
        </div>

        {/* Input Form */}
        <form onSubmit={handleUserInput} className="flex gap-2">
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Type your message here..."
            className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors"
          >
            Send
          </button>
        </form>
      </div>
    </div>
  );
}
</Antartifact>

