class WeatherChatbot:
    def __init__(self):
        self.greeting = "Hi! I can provide weather forecasts for any location - just share your city, postal code, or coordinates."

    def process_location(self, user_input):
        try:
            location = self.validate_location(user_input)
            weather = self.fetch_weather(location)
            return f"Current conditions in {location}: {weather.temp}°F, {weather.humidity}% humidity, winds {weather.wind_speed}mph. Would you like a forecast for the next few days?"
        except LocationError:
            return "I couldn't find that location - try entering a city name or postal code instead."
        except ConnectionError:
            return "Having trouble connecting to weather services - please try again in a moment."
```

