class WeatherBot:
    def __init__(self):
        self.cities = {
            "New York": {"temperature": 50, "condition": "snowy"},
            "Los Angeles": {"temperature": 70, "condition": "sunny"},
            "Chicago": {"temperature": 40, "condition": "windy"},
            "Miami": {"temperature": 80, "condition": "hot"},
            "Seattle": {"temperature": 50, "condition": "rainy"}
        }

    def get_weather(self, city):
        if city in self.cities:
            weather = self.cities[city]
            return f"The weather in {city} is currently {weather['temperature']} degrees with {weather['condition']}."
        else:
            return "Sorry, we don't have weather info for that city."

    def run(self):
        city = input("Where are you from? ")
        print(self.get_weather(city))

if __name__ == "__main__":
    bot = WeatherBot()
    bot.run()
