interface Constraint {
    fun apply(data: Any): Boolean
}

class PrimaryConstraint(private val condition: (Any) -> Boolean) : Constraint {
    override fun apply(data: Any): Boolean {
        return condition(data)
    }
}

class SecondaryConstraint(private val condition: (Any) -> Boolean) : Constraint {
    override fun apply(data: Any): Boolean {
        return condition(data)
    }
}

fun processConstraints(primary: Constraint, secondary: Constraint, data: Any): Boolean {
    if (primary.apply(data)) {
        // Primary constraint is satisfied, secondary constraint can be applied
        return secondary.apply(data)
    } else {
        // Primary constraint is not satisfied, secondary constraint cannot be applied
        return false
    }
}
```

