class WeatherBot:
    def __init__(self):
        self.api_key = "YOUR_OPENWEATHERMAP_API_KEY"
        self.base_url = "http://api.openweathermap.org/data/2.5/forecast?"

    def get_weather(self, location):
        try:
            params = {
                "q": location,
                "appid": self.api_key,
                "units": "metric"
            }
            response = requests.get(self.base_url, params=params)
            return response.json()
        except Exception as e:
            return {"error": str(e)}

    def process_response(self, response):
        if "error" in response:
            return response["error"]
        else:
            return self.format_weather(response)

    def format_weather(self, response):
        weather = response["list"][0]
        return f"Weather in {weather['name']} is like this: {weather['weather'][0]['description']}. It's {weather['main']['temp']} degrees Celsius. Feels like {weather['main']['feels_like']} degrees Celsius."

