**app.py:**

```python
from flask import Flask, render_template, request, jsonify
from flask_wtf import FlaskForm
from wtforms import StringField, SubmitField
from wtforms.validators import DataRequired
from geocoder import get_location
import requests

app = Flask(__name__)
app.config['SECRET_KEY'] = 'secret_key'

class LocationForm(FlaskForm):
    location = StringField('Location', validators=[DataRequired()])
    submit = SubmitField('Submit')

class ForecastForm(FlaskForm):
    forecast_type = StringField('Forecast Type', validators=[DataRequired()])
    submit = SubmitField('Submit')

@app.route('/', methods=['GET', 'POST'])
def index():
    form = LocationForm()
    if form.validate_on_submit():
        location = form.location.data
        user_location = get_location(location)
        if user_location:
            weather_data = get_weather_data(user_location)
            return render_template('forecast.html', weather_data=weather_data)
    return render_template('index.html', form=form)

@app.route('/forecast', methods=['GET', 'POST'])
def forecast():
    form = ForecastForm()
    if form.validate_on_submit():
        forecast_type = form.forecast_type.data
        if forecast_type == 'daily':
            weather_data = get_daily_forecast(user_location)
        elif forecast_type == 'weekly':
            weather_data = get_weekly_forecast(user_location)
        return render_template('forecast.html', weather_data=weather_data)
    return render_template('forecast.html')

def get_weather_data(location):
    api_key = config.OPENWEATHERMAP_API_KEY
    base_url = 'http://api.openweathermap.org/data/2.5/weather'
    params = {
        'q': location,
        'appid': api_key,
        'units': 'metric'
    }
    response = requests.get(base_url, params=params)
    weather_data = response.json()
    return weather_data

def get_daily_forecast(location):
    api_key = config.OPENWEATHERMAP_API_KEY
    base_url = 'http://api.openweathermap.org/data/2.5/forecast'
    params = {
        'q': location,
        'appid': api_key,
        'units': 'metric'
    }
    response = requests.get(base_url, params=params)
    weather_data = response.json()
    return weather_data

def get_weekly_forecast(location):
    api_key = config.OPENWEATHERMAP_API_KEY
    base_url = 'http://api.openweathermap.org/data/2.5/forecast'
    params = {
        'q': location,
        'appid': api_key,
        'units': 'metric'
    }
    response = requests.get(base_url, params=params)
    weather_data = response.json()
    return weather_data

if __name__ == '__main__':
    app.run(debug=True)
```

