import requests
import json

class WeatherChatbot:
    def __init__(self):
        self.api_key = "YOUR_OPENWEATHERMAP_API_KEY"
        self.base_url = "http://api.openweathermap.org/data/2.5/forecast"

    def get_weather_data(self, location):
        params = {
            "q": location,
            "units": "metric",
            "appid": self.api_key
        }

        try:
            response = requests.get(self.base_url, params=params)
            response.raise_for_status()
            return response.json()
        except requests.exceptions.RequestException as e:
            return {"error": f"Error retrieving weather data: {e}"}

    def process_response(self, response):
        if "error" in response:
            return response["error"]

        current_weather = response["list"][0]
        return {
            "current_temperature": current_weather["main"]["temp"],
            "weather_conditions": current_weather["weather"][0]["description"],
            "wind_speed": current_weather["wind"]["speed"],
            "humidity": current_weather["main"]["humidity"],
            "extended_forecast": self.get_extended_forecast(response)
        }

    def get_extended_forecast(self, response):
        extended_forecast = []
        for forecast in response["list"][1:]:
            extended_forecast.append({
                "date": forecast["dt_txt"],
                "temperature": forecast["main"]["temp"],
                "weather_conditions": forecast["weather"][0]["description"]
            })
        return extended_forecast

    def display_weather(self, response):
        print(f"Weather in {response['location']}:")
        print(f"Current temperature: {response['current_temperature']}°C")
        print(f"Weather conditions: {response['weather_conditions']}")
        print(f"Wind speed: {response['wind_speed']} m/s")
        print(f"Humidity: {response['humidity']}%")
        if response.get("extended_forecast"):
            print("\nExtended forecast:")
            for forecast in response["extended_forecast"]:
                print(f"Date: {forecast['date']}")
                print(f"Temperature: {forecast['temperature']}°C")
                print(f"Weather conditions: {forecast['weather_conditions']}")
                print()

def main():
    chatbot = WeatherChatbot()

    # Prompt user for location
    location = input("Enter your location (city name, ZIP code, or GPS coordinates): ")

    # Retrieve weather data
    response = chatbot.get_weather_data(location)

    # Process and display weather data
    if isinstance(response, dict) and "error" not in response:
        response = chatbot.process_response({ "list": response["list"], "city": response["city"] })
        response["location"] = location
        chatbot.display_weather(response)
    else:
        print(response)

if __name__ == "__main__":
    main()
```

