import requests
from datetime import datetime
import geocoder
from typing import Dict, Optional, Tuple

class WeatherBot:
    def __init__(self, api_key: str):
        self.api_key = api_key
        self.base_url = "http://api.openweathermap.org/data/2.5/weather"
        self.forecast_url = "http://api.openweathermap.org/data/2.5/forecast"

    def get_location_coordinates(self, location: str) -> Optional[Tuple[float, float]]:
        try:
            # Handle different location input types
            if self._is_zip_code(location):
                g = geocoder.osm(location)
            elif self._is_coordinates(location):
                lat, lon = map(float, location.split(','))
                return lat, lon
            else:
                g = geocoder.osm(location)

            if g.ok:
                return g.lat, g.lng
            return None
        except Exception as e:
            print(f"Error getting coordinates: {e}")
            return None

    def get_current_weather(self, location: str) -> Dict:
        try:
            coordinates = self.get_location_coordinates(location)
            if not coordinates:
                return {"error": "Location not found. Please try again!"}

            lat, lon = coordinates
            params = {
                "lat": lat,
                "lon": lon,
                "appid": self.api_key,
                "units": "metric"
            }

            response = requests.get(self.base_url, params=params)
            if response.status_code == 200:
                data = response.json()
                return self._format_weather_data(data)
            else:
                return {"error": "Unable to fetch weather data. Please try again later!"}

        except Exception as e:
            return {"error": f"An awesome weather update is what you need, but we encountered an error: {e}"}

    def get_forecast(self, location: str, days: int = 5) -> Dict:
        try:
            coordinates = self.get_location_coordinates(location)
            if not coordinates:
                return {"error": "Location not found. Please try again!"}

            lat, lon = coordinates
            params = {
                "lat": lat,
                "lon": lon,
                "appid": self.api_key,
                "units": "metric"
            }

            response = requests.get(self.forecast_url, params=params)
            if response.status_code == 200:
                data = response.json()
                return self._format_forecast_data(data, days)
            else:
                return {"error": "Unable to fetch forecast data. Please try again later!"}

        except Exception as e:
            return {"error": f"Error fetching forecast: {e}"}

    def _format_weather_data(self, data: Dict) -> Dict:
        return {
            "temperature": f"{data['main']['temp']}°C",
            "conditions": data['weather'][0]['description'],
            "wind_speed": f"{data['wind']['speed']} m/s",
            "humidity": f"{data['main']['humidity']}%",
            "feels_like": f"{data['main']['feels_like']}°C"
        }

    def _format_forecast_data(self, data: Dict, days: int) -> Dict:
        forecast = []
        for item in data['list'][:days * 8:8]:  # Get one reading per day
            forecast.append({
                "date": datetime.fromtimestamp(item['dt']).strftime('%Y-%m-%d'),
                "temperature": f"{item['main']['temp']}°C",
                "conditions": item['weather'][0]['description'],
                "humidity": f"{item['main']['humidity']}%"
            })
        return {"forecast": forecast}

    @staticmethod
    def _is_zip_code(location: str) -> bool:
        return location.isdigit() and len(location) == 5

    @staticmethod
    def _is_coordinates(location: str) -> bool:
        try:
            if ',' in location:
                lat, lon = map(float, location.split(','))
                return -90 <= lat <= 90 and -180 <= lon <= 180
            return False
        except:
            return False

def main():
