import requests
from geopy.geocoders import Nominatim
from datetime import datetime

class WeatherChatBot:
    def __init__(self):
        self.api_key = "YOUR_WEATHER_API_KEY"
        self.geocoder = Nominatim(user_agent="weather_chatbot")

    def greet_user(self):
        current_hour = datetime.now().hour
        if 5 <= current_hour < 12:
            greeting = "Good morning"
        elif 12 <= current_hour < 17:
            greeting = "Good afternoon"
        else:
            greeting = "Good evening"
        return f"{greeting}! I'm your weather assistant. I can provide current weather and forecasts for any location. Would you like to share your location?"

    def process_location(self, location_input):
        try:
            # Handle different location input formats
            if isinstance(location_input, tuple):  # GPS coordinates
                lat, lon = location_input
                location = self.geocoder.reverse((lat, lon))
            else:  # City name or postal code
                location = self.geocoder.geocode(location_input)

            if location:
                return {
                    'lat': location.latitude,
                    'lon': location.longitude,
                    'address': location.address
                }
            return None
        except Exception as e:
            return f"Error processing location: {str(e)}"

    def get_weather(self, coordinates):
        try:
            url = f"https://api.weatherapi.com/v1/forecast.json?key={self.api_key}&q={coordinates['lat']},{coordinates['lon']}&days=3"
            response = requests.get(url)
            data = response.json()

            current = {
                'temp_c': data['current']['temp_c'],
                'humidity': data['current']['humidity'],
                'wind_kph': data['current']['wind_kph'],
                'condition': data['current']['condition']['text']
            }

            forecast = [{
                'date': day['date'],
                'max_temp': day['day']['maxtemp_c'],
                'min_temp': day['day']['mintemp_c'],
                'condition': day['day']['condition']['text']
            } for day in data['forecast']['forecastday']]

            return {'current': current, 'forecast': forecast}
        except Exception as e:
            return f"Error fetching weather data: {str(e)}"

    def format_weather_response(self, weather_data, include_forecast=False):
        current = weather_data['current']
        response = f"Current weather: {current['temp_c']}°C, {current['condition']}\n"
        response += f"Humidity: {current['humidity']}%, Wind: {current['wind_kph']} km/h\n"

        if include_forecast:
            response += "\nForecast for next 3 days:\n"
            for day in weather_data['forecast']:
                response += f"{day['date']}: {day['min_temp']}°C to {day['max_temp']}°C, {day['condition']}\n"

        return response

    def handle_error(self, error_type):
        error_messages = {
            'location_not_found': "I couldn't find that location. Please try providing a city name, postal code, or GPS coordinates.",
            'network_error': "I'm having trouble connecting to the weather service. Please try again in a moment.",
            'invalid_input': "I didn't understand that location format. Please provide a city name, postal code, or GPS coordinates."
        }
        return error_messages.get(error_type, "An unexpected error occurred. Please try again.")

def main():
    bot = WeatherChatBot()
    print(bot.greet_user())

    while True:
        user_input = input("You: ").strip()

        if user_input.lower() in ['exit', 'quit', 'bye']:
            print("Bot: Goodbye! Have a great day!")
            break

        location_data = bot.process_location(user_input)
        if isinstance(location_data, dict):
            weather_data = bot.get_weather(location_data)
            if isinstance(weather_data, dict):
                print("Bot:", bot.format_weather_response(weather_data, True))
            else:
                print("Bot:", bot.handle_error('network_error'))
        else:
            print("Bot:", bot.handle_error('location_not_found'))

if __name__ == "__main__":
    main()
