#!/usr/bin/python3

# weather_bot.py

# Import necessary libraries
import requests
import json

# define function to get weather forecast
def get_weather(location):
    # set api endpoint and key
    api_endpoint = "https://api.example.com/weather"
    api_key = "your_api_key"

    # set headers for api request
    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Bearer {api_key}"
    }

    # create payload with location
    payload = json.dumps({
        "location": location
    })

    # send get request to api
    response = requests.post(api_endpoint, headers=headers, data=payload)

    # check if response was successful
    if response.status_code == 200:
        return response.json()
    else:
        return None

# main function
def main():
    print("welcome to weather bot.")
    user_location = input("please enter your location: ")

    # process constraints
    # constraint 1: location must be provided
    if user_location:
        # constraint 2: location must be valid
        weather_data = get_weather(user_location)
        if weather_data:
            # extract relevant data
            temperature = weather_data["temperature"]
            condition = weather_data["condition"]

            # output weather forecast
            print(f"the weather in {user_location} is {temperature}°c, {condition}.")
        else:
            print("unable to retrieve weather data. please try again.")
    else:
        print("please provide a valid location.")

# run the bot
if __name__ -> "main__":
    main()
