import { useState, useEffect } from 'react';
import { MapPin, Cloud, Wind, Droplets, Calendar } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

export default function WeatherChatbot() {
  const [messages, setMessages] = useState([
    {
      type: 'bot',
      content: 'Bonjour ! Je suis votre assistant météo. Pour commencer, j\'aurais besoin de connaître votre localisation. Vous pouvez me donner le nom de votre ville, votre code postal, ou utiliser la géolocalisation.',
      options: ['Utiliser la géolocalisation', 'Saisir une ville', 'Saisir un code postal']
    }
  ]);
  const [userInput, setUserInput] = useState('');
  const [location, setLocation] = useState(null);
  const [weatherData, setWeatherData] = useState(null);

  const handleLocationPermission = async () => {
    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });

      setLocation({
        latitude: position.coords.latitude,
        longitude: position.coords.longitude
      });

      addMessage('bot', 'Localisation obtenue ! Je récupère les informations météo...');
      // Ici, on simulerait l'appel à l'API météo
    } catch (error) {
      addMessage('bot', 'Désolé, je n\'ai pas pu accéder à votre localisation. Pourriez-vous saisir votre ville ?');
    }
  };

  const addMessage = (type, content, options = []) => {
    setMessages(prev => [...prev, { type, content, options }]);
  };

  const handleUserInput = (input) => {
    addMessage('user', input);
    processUserInput(input);
  };

  const processUserInput = (input) => {
    if (input.toLowerCase().includes('prévisions')) {
      showForecastOptions();
    } else if (!location) {
      // Simulation de la validation de la localisation
      setLocation({ city: input });
      addMessage('bot', `Merci ! Voici la météo pour ${input}:`, [
        'Voir les prévisions du jour',
        'Voir les prévisions de la semaine'
      ]);
      displayWeatherInfo();
    }
  };

  const showForecastOptions = () => {
    addMessage('bot', 'Quel type de prévisions souhaitez-vous consulter ?', [
      'Aujourd\'hui',
      'Cette semaine'
    ]);
  };

  const displayWeatherInfo = () => {
    // Simulation des données météo
    setWeatherData({
      temperature: '22°C',
      humidity: '65%',
      windSpeed: '12 km/h',
      condition: 'Ensoleillé'
    });
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded-lg shadow-lg">
      <div className="space-y-4 h-96 overflow-y-auto mb-4">
        {messages.map((message, index) => (
          <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`rounded-lg p-3 max-w-xs ${
              message.type === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-100'
            }`}>
              <p>{message.content}</p>
              {message.options && (
                <div className="mt-2 space-y-2">
                  {message.options.map((option, optIndex) => (
                    <button
                      key={optIndex}
                      onClick={() => handleUserInput(option)}
                      className="block w-full text-left px-3 py-2 text-sm bg-white rounded hover:bg-gray-50 transition-colors"
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
        ))}

        {weatherData && (
          <Alert className="mt-4">
            <AlertDescription>
              <div className="grid grid-cols-2 gap-4">
                <div className="flex items-center">
                  <Cloud className="mr-2" />
                  <span>{weatherData.condition}</span>
                </div>
                <div className="flex items-center">
                  <Wind className="mr-2" />
                  <span>{weatherData.windSpeed}</span>
                </div>
                <div className="flex items-center">
                  <Droplets className="mr-2" />
                  <span>{weatherData.humidity}</span>
                </div>
                <div className="flex items-center">
                  <Calendar className="mr-2" />
                  <span>{weatherData.temperature}</span>
                </div>
              </div>
            </AlertDescription>
          </Alert>
        )}
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Tapez votre message..."
          className="flex-1 p-2 border rounded-lg"
        />
        <button
          onClick={() => {
            if (userInput.trim()) {
              handleUserInput(userInput);
              setUserInput('');
            }
          }}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          Envoyer
        </button>
      </div>
    </div>
  );
}
</Antartifact>

Ce composant React implémente un chatbot météo avec les fonctionnalités suivantes :

1. Interface utilisateur intuitive avec :
   - Zone de messages défilante
   - Champ de saisie pour les messages
   - Bouton d'envoi
   - Affichage des options cliquables

2. Gestion de la localisation :
   - Support de la géolocalisation
   - Saisie manuelle de la ville ou du code postal
   - Traitement sécurisé des données de localisation

3. Affichage des informations météo :
   - Température actuelle
   - Taux d'humidité
   - Vitesse du vent
   - Conditions météorologiques

4. Fonctionnalités conversationnelles :
   - Messages du bot et de l'utilisateur clairement différenciés
   - Options de réponse cliquables
   - Gestion des différents types de requêtes

