import requests
from datetime import datetime
from typing import Dict, Optional, Tuple
from dataclasses import dataclass
from enum import Enum

class LocationType(Enum):
    CITY = "city"
    ZIP = "zip"
    COORDINATES = "coordinates"

@dataclass
class WeatherData:
    temperature: float
    condition: str
    wind_speed: float
    humidity: int
    timestamp: datetime

class WeatherChatbot:
    def __init__(self, api_key: str):
        self.api_key = api_key
        self.base_url = "https://api.weatherservice.com/v1"  # Example API endpoint

    def process_user_input(self, user_input: str) -> str:
        try:
            location_type = self._detect_location_type(user_input)
            location_data = self._parse_location(user_input, location_type)

            if not location_data:
                return "I couldn't understand that location format. Please provide a city name, ZIP code, or coordinates (lat,lon)."

            weather_data = self._fetch_weather_data(location_data, location_type)
            return self._format_response(weather_data)

        except ConnectionError:
            return "I'm having trouble connecting to the weather service. Please try again later."
        except Exception as e:
            return f"An unexpected error occurred: {str(e)}"

    def _detect_location_type(self, user_input: str) -> LocationType:
        if ',' in user_input and all(p.replace('.','').replace('-','').isdigit() 
                                   for p in user_input.split(',')):
            return LocationType.COORDINATES
        elif user_input.isdigit():
            return LocationType.ZIP
        else:
            return LocationType.CITY

    def _parse_location(self, user_input: str, 
                       location_type: LocationType) -> Optional[Dict[str, str]]:
        if location_type == LocationType.COORDINATES:
            lat, lon = user_input.split(',')
            return {"lat": lat.strip(), "lon": lon.strip()}
        elif location_type == LocationType.ZIP:
            return {"zip": user_input.strip()}
        else:
            return {"city": user_input.strip()}

    def _fetch_weather_data(self, location: Dict[str, str], 
                          location_type: LocationType) -> WeatherData:
        params = {
            "api_key": self.api_key,
            **location
        }

        response = requests.get(f"{self.base_url}/weather", params=params)

        if response.status_code != 200:
            raise ConnectionError("Unable to fetch weather data")

        data = response.json()
        return WeatherData(
            temperature=data["temperature"],
            condition=data["condition"],
            wind_speed=data["wind_speed"],
            humidity=data["humidity"],
            timestamp=datetime.fromtimestamp(data["timestamp"])
        )

    def _format_response(self, weather: WeatherData) -> str:
        return f"""Current Weather Conditions:
Temperature: {weather.temperature}°F
Conditions: {weather.condition}
Wind Speed: {weather.wind_speed} mph
Humidity: {weather.humidity}%
Last Updated: {weather.timestamp.strftime('%I:%M %p')}

Would you like to see the extended forecast? Reply with 'forecast' for a 5-day outlook."""

def main():
    API_KEY = "your_api_key_here"
    chatbot = WeatherChatbot(API_KEY)

    print("Welcome to WeatherBot! Please share your location (city name, ZIP code, or coordinates).")

    while True:
        user_input = input("You: ").strip()

        if user_input.lower() in ['quit', 'exit', 'bye']:
            print("Thank you for using WeatherBot! Have a great day!")
            break

        response = chatbot.process_user_input(user_input)
        print(f"WeatherBot: {response}")

if __name__ == "__main__":
    main()
