const axios = require('axios');

// Set base URL for the API
let urlBase = 'https://api.openweathermap.org/data/2.5/weather';

// Function to get weather data
async function getWeather(cityName) {
    const apiKey = 'YOUR_API_KEY';
    const query = `?q=${cityName}&appid=${apiKey}&units=metric`;
    try {
        const response = await axios.get(urlBase + query);
        // Process and return data
        const data = response.data;
        return {
            temperature: data.main.temp,
            condition: data.weather[0].description,
        };
    } catch (error) {
        return "Unable to retrieve weather data.";
    }
}

// Handle user input
module.exports.handleInput = async function(location) {
    const specificLocation = location.includes(',') ? location : `in ${location}`;
    // Here you would call the getWeather function with the cleaned location input
