public class ConstraintProcessor {
    public static void processConstraints(boolean primary, boolean secondary) {
        if (primary) {
            System.out.println("Primary Constraint satisfied");
            if (secondary) {
                System.out.println("Secondary Constraint satisfied");
            } else {
                System.out.println("Secondary Constraint not considered");
            }
