// Importation des bibliothèques
const express = require('express');
const app = express();
const bodyParser = require('body-parser');
const axios = require('axios');

// Clé API météo
const apiKey = 'VotreCléAPI';
const apiEndpoint = 'https://api.openweathermap.org/data/2.5/weather';

// Middleware de parsing du corps de la requête
app.use(bodyParser.json());

// Route pour récupérer la localisation de l'utilisateur
app.post('/localisation', (req, res) => {
  const { ville, pays } = req.body;
  const url = `${apiEndpoint}?q=${ville},${pays}&appid=${apiKey}&units=metric`;
  axios.get(url)
    .then(response => {
      const données = response.data;
      const météo = {
        température: données.main.temp,
