import requests

class WeatherForecastChatBot:
    def __init__(self):
        self.api_key = "YOUR_OPENWEATHERMAP_API_KEY"
        self.base_url = "http://api.openweathermap.org/data/2.5/weather"

    def process_location(self, location):
        # First constraint: Process the user's location
        try:
            response = requests.get(f"{self.base_url}?q={location}&appid={self.api_key}")
            response.raise_for_status()
            return response.json()
        except requests.exceptions.RequestException as e:
            print(f"Error processing location: {e}")
            return None

    def get_weather_forecast(self, location):
        # Second constraint: Retrieve the weather forecast for the user's location
        if location is not None:
            try:
                response = requests.get(f"{self.base_url}?q={location}&appid={self.api_key}&units=metric")
                response.raise_for_status()
                return response.json()
            except requests.exceptions.RequestException as e:
                print(f"Error retrieving weather forecast: {e}")
                return None

    def get_weather_forecast_like(self, location):
        # In conflicts, first constraint takes precedence
        location_data = self.process_location(location)
        if location_data is not None:
            weather_forecast = self.get_weather_forecast(location)
            if weather_forecast is not None:
                return weather_forecast
        return None

