import requests

def get_weather(location):
    api_key = "YOUR_OPENWEATHERMAP_API_KEY"
    base_url = f"http://api.openweathermap.org/data/2.5/forecast?q={location}&appid={api_key}"
    response = requests.get(base_url)

    if response.status_code == 200:
        weather_data = response.json()
        return weather_data['list']
    else:
        return None

def format_weather_forecast(weather_data):
    formatted_forecast = ""
    for weather in weather_data:
        temperature = weather['main']['temp']
        weather_description = weather['weather'][0]['description']
        formatted_forecast += f"Weather in {weather['name']}: Temperature is {temperature}K, with a description of {weather_description}.\n"
    return formatted_forecast

def main():
    location = input("Enter your location: ")
    weather_data = get_weather(location)
    if weather_data:
        forecast = format_weather_forecast(weather_data)
        print(forecast)
    else:
        print("Failed to retrieve weather data.")

if __name__ == "__main__":
    main()
