            return "Ville non trouvée."

    def generate_french_response(self, city, temperature, condition):
        return f"La météo pour {city} est de {temperature} degrés avec un ciel {condition}."

    def generate_english_response(self, city, temperature, condition):
        return f"The weather forecast for {city} is {temperature} degrees with a {condition} sky."


