**app.py:**

```python
from flask import Flask, render_template, request
from geopy.geocoders import Nominatim
from geopy.distance import geodesic
from requests import get
from bs4 import BeautifulSoup
from geopy.exc import GeocoderTimedOut, GeocoderServiceError

app = Flask(__name__)

# Initialize geolocator
geolocator = Nominatim(user_agent="weather_forecast_assistant")

# Define API keys for OpenWeatherMap
openweathermap_api_key = "YOUR_OPENWEATHERMAP_API_KEY"

# Define a function to get user's location
def get_user_location():
    location = request.args.get("location")
    if location:
        try:
            return geolocator.geocode(location)
        except (GeocoderTimedOut, GeocoderServiceError):
            return None
    else:
        return None

# Define a function to get weather data
def get_weather_data(lat, lon):
    base_url = f"http://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={openweathermap_api_key}"
    response = get(base_url)
    if response.status_code == 200:
        return response.json()
    else:
        return None

# Define a function to get forecast data
def get_forecast_data(lat, lon):
    base_url = f"http://api.openweathermap.org/data/2.5/forecast?lat={lat}&lon={lon}&appid={openweathermap_api_key}"
    response = get(base_url)
    if response.status_code == 200:
        return response.json()
    else:
        return None

# Define a route for the home page
@app.route("/", methods=["GET", "POST"])
def index():
    if request.method == "POST":
        location = get_user_location()
        if location:
            lat = location.latitude
            lon = location.longitude
            weather_data = get_weather_data(lat, lon)
            if weather_data:
                return render_template("forecast.html", weather_data=weather_data)
            else:
                return "Error: Unable to retrieve weather data."
        else:
            return "Error: Unable to retrieve location."
    else:
        return render_template("index.html")

# Define a route for the forecast page
@app.route("/forecast", methods=["GET", "POST"])
def forecast():
    if request.method == "POST":
        location = get_user_location()
        if location:
            lat = location.latitude
            lon = location.longitude
            forecast_data = get_forecast_data(lat, lon)
            if forecast_data:
                return render_template("forecast.html", forecast_data=forecast_data)
            else:
                return "Error: Unable to retrieve forecast data."
        else:
            return "Error: Unable to retrieve location."
    else:
        return render_template("forecast.html")

# Run the application
if __name__ == "__main__":
    app.run(debug=True)
```

