app.js
config.js
models/
Location.js
Weather.js
routes/
index.js
weather.js
utils/
location.js
weather.js
package.json
```

**app.js**
```javascript
const express = require('express');
const app = express();
const port = 3000;
const locationService = require('./utils/location');
const weatherService = require('./utils/weather');

app.use(express.json());

app.get('/', (req, res) => {
  res.send('Welcome to the Weather Chatbot!');
});

app.post('/location', (req, res) => {
  const location = req.body;
  locationService.getLocation(location, (err, locationData) => {
    if (err) {
      res.status(400).send({ error: 'Invalid location' });
    } else {
      const weatherData = weatherService.getWeather(locationData);
      res.send(weatherData);
    }
  });
});

app.get('/forecast', (req, res) => {
  const location = req.query.location;
  const forecastType = req.query.forecastType;
  const weatherData = weatherService.getForecast(location, forecastType);
  res.send(weatherData);
});

app.listen(port, () => {
  console.log(`Server listening on port ${port}`);
});
```

