**utils/location.js**
```javascript
const axios = require('axios');

const getLocation = (location, callback) => {
  axios.get(`https://example.com/location-service?city=${location.city}&zipCode=${location.zipCode}`)
    .then(response => {
      const locationData = response.data;
      callback(null, locationData);
    })
    .catch(error => {
      callback(error);
    });
};

module.exports = { getLocation };
```

**utils/weather.js**
```javascript
const axios = require('axios');

const getWeather = (locationData, callback) => {
  axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${locationData.latitude}&lon=${locationData.longitude}&appid=${config.openWeatherMapApiKey}`)
    .then(response => {
      const weatherData = response.data;
      const currentTemperature = weatherData.main.temp;
      const humidity = weatherData.main.humidity;
      const windSpeed = weatherData.wind.speed;
      const description = weatherData.weather[0].description;
      const weather = new Weather(currentTemperature, humidity, windSpeed, description);
      callback(null, weather);
    })
    .catch(error => {
      callback(error);
    });
};

const getForecast = (location, forecastType, callback) => {
  axios.get(`https://api.openweathermap.org/data/2.5/forecast?lat=${location.latitude}&lon=${location.longitude}&appid=${config.openWeatherMapApiKey}`)
    .then(response => {
      const forecastData = response.data;
      const forecast = [];
      forecastData.list.forEach((item, index) => {
        const currentTemperature = item.main.temp;
        const humidity = item.main.humidity;
        const windSpeed = item.wind.speed;
        const description = item.weather[0].description;
        const weather = new Weather(currentTemperature, humidity, windSpeed, description);
        forecast.push(weather);
      });
      callback(null, forecast);
    })
    .catch(error => {
      callback(error);
    });
};

module.exports = { getWeather, getForecast };
```

