### script.js

```javascript
// Import required libraries
const axios = require('axios');
const { caesar, uncaesar } = require('caesar-cipher');

// Weather API endpoint URL
const weatherApiUrl = 'https://api.openweathermap.org/data/2.5/weather';

// OpenWeatherMap API key
const apiKey = 'YOUR_API_KEY_HERE';

// Function to get user location
function getLocation() {
    // Check if user has granted location permissions
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(position => {
            const lat = position.coords.latitude;
            const lon = position.coords.longitude;
            // Use location services to get weather data
            getWeatherData(lat, lon);
        });
    } else {
        // Request user to enter city name or ZIP code
        const locationInput = document.getElementById('location-input');
        const getLocationBtn = document.getElementById('get-location-btn');
        getLocationBtn.addEventListener('click', () => {
            const location = locationInput.value;
            // Use entered location to get weather data
            getWeatherData(location);
        });
    }
}

// Function to get weather data for a given location
function getWeatherData(location) {
    const weatherApiUrlWithParams = `${weatherApiUrl}?q=${location}&appid=${apiKey}&units=metric`;
    axios.get(weatherApiUrlWithParams)
        .then(response => {
            const weatherData = response.data;
            displayWeatherData(weatherData);
        })
        .catch(error => console.error(error));
}

// Function to display weather data
function displayWeatherData(weatherData) {
    const weatherContainer = document.getElementById('weather-container');
    weatherContainer.innerHTML = '';
    const {
        main: { temp, humidity },
        weather: [{ description, icon }],
        wind: { speed }
    } = weatherData;
    const weatherCard = document.createElement('div');
    weatherCard.classList.add('weather-card');
    weatherCard.innerHTML = `
        <h2>Weather in ${weatherData.name}</h2>
        <p>Temperature: ${temp}°C</p>
        <p>Humidity: ${humidity}%</p>
        <p>Wind Speed: ${speed} m/s</p>
        <p>Description: ${description}</p>
        <img src="https://openweathermap.org/img/wn/${icon}@2x.png" alt="Weather Icon">
    `;
    weatherContainer.appendChild(weatherCard);
}

// Function to display daily forecast
function displayDailyForecast(weatherData) {
    const weatherContainer = document.getElementById('weather-container');
    weatherContainer.innerHTML = '';
    weatherData.daily.forEach(day => {
        const weatherCard = document.createElement('div');
        weatherCard.classList.add('weather-card');
        weatherCard.innerHTML = `
            <h2>Date: ${day.dt}</h2>
            <p>Temperature: ${day.temp.day}°C</p>
            <p>Humidity: ${day.humidity}%</p>
            <p>Wind Speed: ${day.wind.speed} m/s</p>
            <p>Description: ${day.weather[0].description}</p>
            <img src="https://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png" alt="Weather Icon">
        `;
        weatherContainer.appendChild(weatherCard);
    });
}

// Function to display weekly forecast
function displayWeeklyForecast(weatherData) {
    const weatherContainer = document.getElementById('weather-container');
    weatherContainer.innerHTML = '';
    weatherData.daily.forEach(day => {
        const weatherCard = document.createElement('div');
        weatherCard.classList.add('weather-card');
        weatherCard.innerHTML = `
            <h2>Date: ${day.dt}</h2>
            <p>Temperature: ${day.temp.day}°C</p>
            <p>Humidity: ${day.humidity}%</p>
            <p>Wind Speed: ${day.wind.speed} m/s</p>
            <p>Description: ${day.weather[0].description}</p>
            <img src="https://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png" alt="Weather Icon">
        `;
        weatherContainer.appendChild(weatherCard);
    });
}

// Event listeners for daily and weekly forecast buttons
document.getElementById('daily-forecast-btn').addEventListener('click', () => {
    const weatherContainer = document.getElementById('weather-container');
    weatherContainer.innerHTML = '';
    // Fetch daily forecast data from API
    axios.get(`${weatherApiUrl}?q=${document.getElementById('location-input').value}&appid=${apiKey}&units=metric&cnt=5`)
        .then(response => {
            const weatherData = response.data;
            displayDailyForecast(weatherData);
        })
        .catch(error => console.error(error));
});

document.getElementById('weekly-forecast-btn').addEventListener('click', () => {
    const weatherContainer = document.getElementById('weather-container');
    weatherContainer.innerHTML = '';
    // Fetch weekly forecast data from API
    axios.get(`${weatherApiUrl}?q=${document.getElementById('location-input').value}&appid=${apiKey}&units=metric&cnt=40`)
        .then(response => {
            const weatherData = response.data;
            displayWeeklyForecast(weatherData);
        })
        .catch(error => console.error(error));
});

// Initialize chatbot
getLocation();

