import requests
import json

def get_weather(location):
    try:
        api_key = "YOUR_OPENWEATHERMAP_API_KEY"
        base_url = "http://api.openweathermap.org/data/2.5/weather?"
        params = {
            "q": location,
            "appid": api_key,
            "units": "metric"
        }
        response = requests.get(base_url, params=params)
        weather_data = response.json()
        return weather_data
    except requests.exceptions.RequestException as e:
        return {"error": "Failed to retrieve weather data"}

def display_weather(weather_data):
    if "error" in weather_data:
        print("Error: ", weather_data["error"])
    else:
        current_temperature = weather_data["main"]["temp"]
        weather_conditions = weather_data["weather"][0]["description"]
        wind_speed = weather_data["wind"]["speed"]
        humidity = weather_data["main"]["humidity"]
        print(f"Current temperature: {current_temperature}°C")
        print(f"Weather conditions: {weather_conditions}")
        print(f"Wind speed: {wind_speed} m/s")
        print(f"Humidity: {humidity}%")

def extended_forecast(location):
    try:
        api_key = "YOUR_OPENWEATHERMAP_API_KEY"
        base_url = "http://api.openweathermap.org/data/2.5/forecast?"
        params = {
            "q": location,
            "appid": api_key,
            "units": "metric"
        }
        response = requests.get(base_url, params=params)
        forecast_data = response.json()
        return forecast_data
    except requests.exceptions.RequestException as e:
        return {"error": "Failed to retrieve forecast data"}

def display_extended_forecast(forecast_data):
    if "error" in forecast_data:
        print("Error: ", forecast_data["error"])
    else:
        for i in range(0, 40, 8):
            date = forecast_data["list"][i]["dt_txt"]
            temperature = forecast_data["list"][i]["main"]["temp"]
            weather_conditions = forecast_data["list"][i]["weather"][0]["description"]
            print(f"{date}: {temperature}°C, {weather_conditions}")

def main():
    print("Welcome to the Weather Forecast Chatbot!")
    location = input("Please enter your current location (city name, ZIP code, or GPS coordinates): ")
    weather_data = get_weather(location)
    display_weather(weather_data)
    while True:
        choice = input("Would you like to view an extended forecast for the next few days? (yes/no): ")
        if choice.lower() == "yes":
            forecast_data = extended_forecast(location)
            display_extended_forecast(forecast_data)
            break
        elif choice.lower() == "no":
            break
        else:
            print("Invalid choice. Please try again.")

if __name__ == "__main__":
    main()
