import requests
from datetime import datetime
import os
from dotenv import load_dotenv

class WeatherBot:
    def __init__(self):
        load_dotenv()
        self.api_key = os.getenv('WEATHER_API_KEY')
        self.base_url = "http://api.openweathermap.org/data/2.5/weather"

    def get_weather(self, location):
        try:
            params = {
                'q': location,
                'appid': self.api_key,
                'units': 'metric'
            }
            response = requests.get(self.base_url, params=params)
            data = response.json()

            if response.status_code == 200:
                weather_info = {
                    'temperature': data['main']['temp'],
                    'description': data['weather'][0]['description'],
                    'humidity': data['main']['humidity'],
                    'wind_speed': data['wind']['speed']
                }
                return self._format_response(weather_info)
            else:
                return "Sorry, I couldn't find weather data for that location."

        except Exception as e:
            return f"An error occurred: {str(e)}"

    def _format_response(self, weather_info):
        return (
            f"Current weather:\n"
            f"Temperature: {weather_info['temperature']}°C\n"
            f"Conditions: {weather_info['description'].capitalize()}\n"
            f"Humidity: {weather_info['humidity']}%\n"
            f"Wind Speed: {weather_info['wind_speed']} m/s"
        )

def main():
    bot = WeatherBot()
    print("Welcome to WeatherBot! Type 'quit' to exit.")

    while True:
        user_input = input("\nEnter a city name: ").strip()
        if user_input.lower() == 'quit':
            print("Goodbye!")
            break

        response = bot.get_weather(user_input)
        print(response)

if __name__ == "__main__":
    main()
