import { useState, useEffect } from 'react';
import { MessageSquare, MapPin, Sun, Cloud, Wind } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

export default function WeatherChatbot() {
  const [messages, setMessages] = useState([
    { type: 'bot', content: 'Hello! I can help you check the weather. Could you please share your location? You can enter a city name or ZIP code.' }
  ]);
  const [userInput, setUserInput] = useState('');
  const [location, setLocation] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleUserInput = (e) => {
    e.preventDefault();
    if (!userInput.trim()) return;

    // Add user message to chat
    const newMessages = [...messages, { type: 'user', content: userInput }];
    setMessages(newMessages);

    // Process location input
    if (!location) {
      handleLocationInput(userInput);
    } else {
      handleWeatherQuery(userInput);
    }

    setUserInput('');
  };

  const handleLocationInput = (input) => {
    setLoading(true);
    // Simulate location validation
    setTimeout(() => {
      setLocation(input);
      setMessages(prev => [...prev, {
        type: 'bot',
        content: `Thanks! I've set your location to ${input}. What would you like to know about the weather? You can ask about:
        • Current conditions
        • Today's forecast
        • Weekly forecast
        • Temperature
        • Humidity
        • Wind speed`
      }]);
      setLoading(false);
    }, 1000);
  };

  const handleWeatherQuery = (query) => {
    setLoading(true);
    // Simulate weather API response
    setTimeout(() => {
      const weatherResponse = generateMockWeatherResponse(query);
      setMessages(prev => [...prev, { type: 'bot', content: weatherResponse }]);
      setLoading(false);
    }, 1000);
  };

  const generateMockWeatherResponse = (query) => {
    const responses = {
      'current': `Current conditions in ${location}:
      Temperature: 72°F
      Humidity: 45%
      Wind: 8 mph
      Conditions: Partly cloudy`,
      'forecast': `Today's forecast for ${location}:
      High: 75°F
      Low: 60°F
      Chance of rain: 20%`,
      'weekly': `7-day forecast for ${location} shows mild temperatures with a chance of rain on Wednesday.`
    };

    const queryLower = query.toLowerCase();
    if (queryLower.includes('current')) return responses.current;
    if (queryLower.includes('forecast') && queryLower.includes('week')) return responses.weekly;
    if (queryLower.includes('forecast')) return responses.forecast;
    return `I'm not sure about that. You can ask about current conditions, today's forecast, or the weekly forecast.`;
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded-lg shadow-lg">
      <div className="flex items-center gap-2 mb-4">
        <MessageSquare className="text-blue-500" />
        <h2 className="text-xl font-semibold">Weather Assistant</h2>
      </div>

      <div className="h-96 overflow-y-auto mb-4 space-y-4">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-[80%] p-3 rounded-lg ${
                message.type === 'user'
                  ? 'bg-blue-500 text-white'
                  : 'bg-gray-100 text-gray-800'
              }`}
            >
              {message.content}
            </div>
          </div>
        ))}
        {loading && (
          <div className="flex justify-start">
            <div className="bg-gray-100 p-3 rounded-lg">
              <div className="animate-pulse">Typing...</div>
            </div>
          </div>
        )}
      </div>

      <form onSubmit={handleUserInput} className="flex gap-2">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..."
          className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          Send
        </button>
      </form>

      {location && (
        <Alert className="mt-4">
          <MapPin className="h-4 w-4" />
          <AlertDescription>
            Current location: {location}
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
}
</Antartifact>

